import React, { Component } from 'react';
import { Button, FlatList, Text, View, Modal, TextInput } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import DisplayAlert from './DisplayAlert';

const getData = async (done) => {
    try {
        const jsonValue = await AsyncStorage.getItem('@spacebook_details');
        const data = JSON.parse(jsonValue);
        return done(data);
    } catch (e) {
        console.log(e);
    }
    return null;
};

const displayAlert = new DisplayAlert();

class FeedScreen extends Component {
    constructor(props){
        super(props);

        this.state = {
            data: {},
            posts: [],
            modalVisible: false,
            postText: ''
        }
    }

    componentDidMount(){
        const { navigation } = this.props;
        getData((data) => {
            this.setState({
                data
            });
            this.getPosts();
            navigation.addListener('focus', () => {
                this.getPosts();
            });
        });
    }

    // get all ids from friends (for loop stuff)

    getPosts(){
        const { data } = this.state;
        fetch(`http://localhost:3333/api/1.0.0/user/${ data.id }/post`, {
            method: 'GET',
            headers: {
                'X-Authorization': data.token
            }
        })
        .then((response) => response.json())
        .then((responseJson) => {
            this.setState({
                posts: responseJson
            });
        })
        .catch((error) => {
            console.log(error);
        });
    }

    uploadPost(){
        const { data, postText } = this.state;
        fetch(`http://localhost:3333/api/1.0.0/user/${ data.id }/post`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'X-Authorization': data.token
            },
            body: JSON.stringify({
                text: postText
            })
        })
        .then((response) => {
            if (response.status === 201){
                displayAlert.displayAlert('Post uploaded.');
            }
            else {
                displayAlert.displayAlert(`${ response.statusText } ${ response.status }`)
            }
            this.getPosts();
        })
    }

    displayDate(timestamp) {
        const date = new Date(timestamp);
        return `${(`0${  date.getHours()}`).slice(-2)  }:${  (`0${  date.getMinutes()}`).slice(-2)}`;
    }

    render(){
        const { posts, modalVisible, postText } = this.state;
        return (
            <View>
                <Modal animationType='slide' 
                    transparent
                    visible={modalVisible} 
                    onRequestClose={() => {
                        // displayAlert.displayAlert('')
                        this.setState({modalVisible: !modalVisible});
                    }}
                >
                    <View>
                        <TextInput placeholder='Enter text...' onChangeText={(newPostText) => this.setState({postText: newPostText})} value={postText}/>
                        <Button title='Post' onPress={() => {
                            this.uploadPost();
                            this.setState({modalVisible: false})
                        }}/>
                    </View>
                </Modal>
                <View>
                    <FlatList extraData={this.state} data={posts}
                        renderItem={({item}) => (
                            <View>
                                {/* <Text>{item.author.first_name} {item.author.last_name} {Date(item.timestamp).getDay()} {Date(item.timestamp).getMonth()} {Date(item.timestamp).getYear()} {Date(item.timestamp).getHours()} {Date(item.timestamp).getMinutes()}</Text> */}
                                {/* <Text>{Date(item.timestamp).getTime()}</Text> */}
                                <Text><Moment date={item.timestamp} format="ddd.m.yyyy hh:mm"/></Text>
                                <Text>{this.displayDate(item.timestamp)}</Text>
                                <Text>{item.text}</Text>
                            </View>
                        )}
                    />
                    <Button title='Add post' onPress={() => this.setState({modalVisible: true})}/>
                </View>
            </View>
        )
    }
}

FeedScreen.propTypes = {
    navigation: PropTypes.shape({
        navigate: PropTypes.func.isRequired,
        addListener: PropTypes.func.isRequired
    }).isRequired
}

export default FeedScreen;
