import React, { Component } from 'react';
import { Button, FlatList, Text, View, Modal, TextInput, Pressable, ActivityIndicator } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import DisplayAlert from './DisplayAlert';

const getData = async (done) => {
    try {
        const jsonValue = await AsyncStorage.getItem('@spacebook_details');
        const data = JSON.parse(jsonValue);
        return done(data);
    } catch (e) {
        console.log(e);
    }
    return null;
};

class FeedScreen extends Component {
    constructor(props){
        super(props);

        this.state = {
            isLoading: true,
            ids: []
        }
    }

    componentDidMount(){
        const { navigation } = this.props;
        getData((data) => {
            this.setState({
                data
            });
            this.getIds();
            navigation.addListener('focus', () => {
                this.getIds();
            });
        });
    }

    getIds(){
        const { data } = this.state;
        const idList = [];
        console.log(data.id);
        fetch(`http://localhost:3333/api/1.0.0/user/${ data.id }/friends`, {
            method: 'GET',
            headers: {
                'X-Authorization': data.token
            }
        })
        .then((response) => response.json())
        .then((responseJson) => {
            for (let i = 0; i < responseJson.length; i+=1){
                idList.push(responseJson[i].user_id);
            }
            console.log(idList.length);
            this.setState({ids: idList});
            this.getPosts();
        })
        .catch((error) => {
            console.log(error);
        });
    }

    render() {
        if (this.state.isLoading){
            return (
                <View>
                    <ActivityIndicator
                        size="large"
                        color="#00ff00"
                    />
                </View>
            );
        } 
        return (
            <View>
                <Text>FeedScreen</Text>
            </View>
        )
    }
}

FeedScreen.propTypes = {
    navigation: PropTypes.shape({
        navigate: PropTypes.func.isRequired,
        addListener: PropTypes.func.isRequired
    }).isRequired
}

export default FeedScreen;
