import React, { Component } from "react";
import { Button, FlatList, Text, View } from 'react-native';
import AsyncStorage from "@react-native-async-storage/async-storage";

import DisplayAlert from "./DisplayAlert";

const displayAlert = new DisplayAlert();

const getData = async (done) => {
    try {
        const jsonValue = await AsyncStorage.getItem('@spacebook_details');
        const data = JSON.parse(jsonValue);
        return done(data);
    } catch (e) {
        console.log(e);
    }
};

class Profile extends Component {
    constructor(props) {
        super(props);

        this.state = {
            login_info: {},
            userData: []
        };
    }

    componentDidMount() {
        getData((data) => {
            this.setState({
                login_info: data
            });
        });
        this.getUserData();
    }

    getUserData(){
        return fetch('http://localhost:3333/api/1.0.0/user/' + this.login_info.id, {
            method: 'get',
            headers: {
                'X-Authorization': this.state.login_info.token
            },
            body: {}
        })
        .then((response) => response.json())
        .then((responseJson) => {
            this.setState({
                userData: responseJson
            });
        })
        .catch((error) => {
            console.log(error);
        });
    }

    logout(nav) {
        fetch('http://localhost:3333/api/1.0.0/logout', {
            method: 'post',
            headers: {
                'X-Authorization': this.state.login_info.token
            },
            body: {}
        })
        .then((response) => {
            console.log(response.status);
            if (response.status == 200){
                displayAlert.displayAlert('Logged out.');
                nav.navigate('Login');
            }
            else if (response.status == 401 || response.status == 500){
                displayAlert.displayAlert(response.statusText);
            }
        })
        .catch((error) => {
            console.log(error);
        });
    }

    render() {
        const nav = this.props.navigation;
        return (
            <View>
                <Text>Profile</Text>
                <Text>Login id: {this.state.login_info.id}</Text>
                <Text>Login token: {this.state.login_info.token}</Text>
                <View>
                    <FlatList data={this.state.userData}
                        renderItem={({item}) => (
                            <View>
                                <Text>{item.first_name}</Text>
                                <Text>{item.last_name}</Text>
                                <Text>{item.email}</Text>
                                <Text>{item.friend_count}</Text>
                            </View>
                        )}
                        keyExtractor={(item, index) => item.id.toString()}
                    />
                </View>
                <Button title="Logout" onPress={() => this.logout(nav)}/>
            </View>
        );
    }
}


export default Profile;
