import React, { Component, useEffect, useState } from 'react';
import { Button, FlatList, Text, View } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

import DisplayAlert from './DisplayAlert';
import { NavigationContainer } from '@react-navigation/native';

const displayAlert = new DisplayAlert();

const getData = async (done) => {
    try {
        const jsonValue = await AsyncStorage.getItem('@spacebook_details');
        const data = JSON.parse(jsonValue);
        return done(data);
    } catch (e) {
        console.log(e);
    }
};


class ProfileScreen extends Component {
    constructor(props) {
        super(props);

        this.state = {
            test: 'abc',
            login_info: {},
            userData: {},
        };
    }

    componentDidMount() {
        getData((data) => {
            this.setState({
                login_info: data,
            });
            this.getUserData();
            this.focusListener = this.props.navigation.addListener('focus', () => {
                this.getUserData();
            })
        });
    }

    async getUserData() {
        let response = await fetch(
            'http://localhost:3333/api/1.0.0/user/' + this.state.login_info.id,
            {
                method: 'get',
                headers: {
                    'X-Authorization': this.state.login_info.token,
                },
            }
        );
        let result = await response.json();
        this.setState({
            userData: result,
        });
    }

    logout(nav) {
        fetch('http://localhost:3333/api/1.0.0/logout', {
            method: 'post',
            headers: {
                'X-Authorization': this.state.login_info.token,
            },
            body: {},
        })
            .then((response) => {
                console.log(response.status);
                if (response.status == 200) {
                    displayAlert.displayAlert('Logged out.');
                    nav.navigate('Login');
                } else if (response.status == 401 || response.status == 500) {
                    displayAlert.displayAlert(response.statusText);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }

    settings(nav){
        nav.navigate('Settings', {onGoBack: () => this.componentDidMount()});
    }

    render() {
        const nav = this.props.navigation;
        return (
            <View>
                <Text>Profile</Text>
                <Text>Login id: {this.state.login_info.id}</Text>
                <Text>Login token: {this.state.login_info.token}</Text>
                <View>
                    <Text>{this.state.userData.first_name}</Text>
                    <Text>{this.state.userData.last_name}</Text>
                    <Text>{this.state.userData.email}</Text>
                    <Text>{this.state.userData.friend_count}</Text>
                </View>
                <View>
                    <Button title="Friends" onPress={() => this.friends()} />
                    <Button title="Account Settings" onPress={() => this.settings(nav)} />
                </View>
                <Button title="Logout" onPress={() => this.logout(nav)} />
            </View>
        );
    }
}

export default ProfileScreen;
