import React, { Component } from 'react';
import { Box, NativeBaseProvider, Text, VStack, Button, Icon, Pressable, HStack, Image, FlatList, Divider, Fab, Modal, FormControl, Input } from 'native-base';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import Storage from './Storage'
import DisplayAlert from './DisplayAlert';

const asyncStorage = new Storage();

const displayAlert = new DisplayAlert();

class FriendsScreen extends Component {
    constructor(props){
        super(props);

        this.state = {
            data: {},
            friends: []
        }
    }

    componentDidMount(){
        const { navigation } = this.props;
        this.setState({friends: []});
        asyncStorage.getData((data) => {
            this.setState({
                data
            });
            this.getFriends();
            navigation.addListener('focus', () => {
                this.setState({friends: []});
                this.getFriends();
            });
        });
    }

    getFriends(){
        const { data } = this.state; 
        fetch(`http://localhost:3333/api/1.0.0/user/${ data.id }/friends`, {
            method: 'GET',
            headers: {
                'X-Authorization': data.token
            }
        })
        .then((response) => {
            if (response.status === 401){
                return Promise.reject(new Error(`Unauthorised. Status: ${  response.status}`));
            }
            if (response.status === 403){
                return Promise.reject(new Error(`Can only view the friends of yourself or your friends. Status: ${  response.status}`));
            }
            if (response.status === 404){
                return Promise.reject(new Error(`Not found. Status: ${  response.status}`));
            }
            if (response.status === 500){
                return Promise.reject(new Error(`Server error. Status: ${ response.status }`));
            } 
            return response.json()
        })
        .then((responseJson) => {
            this.setState({
                friends: responseJson
            });
        })
        .catch((error) => {
            displayAlert.displayAlert(error);
        })
    }

    getProfileImage(userId) {
        const { data } = this.state;
        return fetch(`http://localhost:3333/api/1.0.0/user/${  userId  }/photo`, {
            method: 'GET',
            headers: {
                'X-Authorization': data.token
            }
        })
        .then((response) => {
            if (response.status === 401){
                return Promise.reject(new Error(`Unauthorised. Status: ${  response.status}`));
            }
            if (response.status === 404){
                return Promise.reject(new Error(`Not found. Status: ${  response.status}`));
            }
            if (response.status === 500){
                return Promise.reject(new Error(`Server error. Status: ${ response.status }`));
            }
            return response.blob();
        })
        .then((responseBlob) => {
            // if (responseJson.length > 0){

            // }
            const img = URL.createObjectURL(responseBlob);
            return img;
        });
    }

    viewPost(item){

        const img = this.getProfileImage(item.user_id)
        if (img !== null){
            return (
            <HStack>
                <Box>
                    <HStack space={5} justifyContent="flex-end">
                        <Image source={{uri: img}} size={50} borderRadius="100" alt="Profile Picture"/>
                        <VStack>
                            <Text  bold>{item.user_givenname} {item.user_familyname}</Text>
                            <Text>{item.text}</Text>
                        </VStack>
                    </HStack>
                </Box>
            </HStack>
            );
        }
        return null;
    }

    render() {
        const { navigation } = this.props;
        const { friends } = this.state;
        return (
            <NativeBaseProvider>
                <Box>
                    <FlatList
                        data={friends}
                        renderItem={({item}) => (
                            <Box style={{borderWidth: 1, borderColor: 'gray'}}>
                                {this.viewPost(item)}
                            </Box>
                        )}
                        keyExtractor={(item, index) => index.toString()}
                    />
                </Box>
                <Box>  
                    <Button title='Find friends' onPress={() => navigation.navigate('FindFriends')}/>   
                    <Button title='Friend requests' onPress={() => navigation.navigate('FriendRequests')}/>
                </Box>
            </NativeBaseProvider>
        );
    }
}

FriendsScreen.propTypes = {
    navigation: PropTypes.shape({
        navigate: PropTypes.func.isRequired,
        addListener: PropTypes.func.isRequired
    }).isRequired
}

export default FriendsScreen;
